mapper.select:
SELECT e.id, e.name, CASE e.gender WHEN 1 THEN 'Male' WHEN 2 THEN 'Female' END AS gender, e.image, jt.name AS jobTitle, e.hire_date, e.update_time, d.name AS deptName FROM employee e LEFT JOIN department d ON e.dept_id = d.id AND d.is_deleted = 0 LEFT JOIN job_title jt ON e.job_title = jt.id AND jt.is_deleted = 0
e.is_deleted = 0
AND e.name LIKE CONCAT('%', #{name}, '%')
AND e.gender = #{gender}
AND e.hire_date BETWEEN #{startDate} AND #{endDate}
ORDER BY e.update_time DESC

mapper.update:
UPDATE employee SET is_deleted = 1, update_time = NOW() WHERE is_deleted = 0
AND id IN
#{id}

mapper.insert:
INSERT INTO employee
( image, username, name, gender, job_title, hire_date, dept_id,
phone, salary, create_time, update_time, is_deleted
)
VALUES
( #{image}, #{username}, #{name}, #{gender}, #{jobTitle}, #{hireDate}, #{deptId},
#{phone}, #{salary}, #{createTime}, #{updateTime}, #{isDeleted}
)
