mapper.select:
select e.id, e.name, e.gender, e.image, e.job_title, e.hire_date, e.update_time, d.name as dept_name from employee e left join department d on e.dept_id = d.id
e.is_deleted = 0 and d.is_deleted = 0
and e.name like concat('%', #{name},'%')
and e.gender = #{gender}
and e.hire_date between #{startDate} and #{endDate}
order by e.update_time desc

mapper.update:
update employee set is_deleted = 1 where id in
#{id}
and is_deleted = 0

mapper.insert:
INSERT INTO employee
( image, username, name, gender, job_title, hire_date, dept_id,
phone, salary, create_time, update_time, is_deleted
)
VALUES
( #{image}, #{username}, #{name}, #{gender}, #{jobTitle}, #{hireDate}, #{deptId},
#{phone}, #{salary}, #{createTime}, #{updateTime}, #{isDeleted}
)

mapper.select:
SELECT id,
username
FROM employee
WHERE is_deleted = 0
AND username = #{username}
AND password = #{password};
