mapper.select:
SELECT s.id, s.name AS student_name, s.no, CASE s.gender WHEN 1 THEN 'Male' WHEN 2 THEN 'Female' END AS gender, el.name AS highest_education, c.name AS clazz_name, s.intake_date, s.update_time FROM student s LEFT JOIN clazz c ON s.clazz_id = c.id LEFT JOIN education_level el ON s.highest_education = el.id
s.is_deleted = 0 AND c.is_deleted = 0 AND el.is_deleted = 0
AND s.name LIKE CONCAT('%', #{name}, '%')
AND el.id = #{highestEducation}
AND s.clazz_id = #{clazzId}
ORDER BY s.update_time DESC

mapper.update:
update student set is_deleted = 1 where id in
#{id}
and is_deleted = 0
